import { useEffect, useRef, useState } from "react";
import StarRating from "./StarRating";
import { useMovies } from "./useMovies";

const average = (arr) =>
  arr.reduce((acc, cur, i, arr) => acc + cur / arr.length, 0);

const KEY = "70ca7728";

export default function App() {
  const [query, setQuery] = useState("");
  const [selectedId, setSelectedId] = useState(null);

  useMovies(query);

  // const [watched, setWatched] = useState([]);
  const [watched, setWatched] = useState(function () {
    //pure function => can not receive any argument
    const storedValue = localStorage.getItem("watched");
    return JSON.parse(storedValue);
  });

  function handleSelectMovie(id) {
    setSelectedId((selectedId) => (id === selectedId ? null : id));
  }

  function handleCloseMovie() {
    setSelectedId(null);
  }

  function handleAddWatched(movie) {
    setWatched((watched) => [...watched, movie]);

    // localStorage.setItem("watched", JSON.stringify([...watched, movie]));
  }

  function handleDeleteWatched(id) {
    setWatched((watched) => watched.filter((movie) => movie.imdbID !== id));
  }

  useEffect(
    function () {
      localStorage.setItem("watched", JSON.stringify(watched));
    },
    [watched]
  );

  function Loader() {
    return <p className="loader">Loading...</p>;
  }

  function ErrorMessage({ message }) {
    return (
      <p className="error">
        <span>‚õîÔ∏è</span> {message}
      </p>
    );
  }

  function NavBar({ children }) {
    return (
      <nav className="nav-bar">
        <Logo />
        {children}
      </nav>
    );
  }

  function Logo() {
    return (
      <div className="logo">
        <span role="img">üçø</span>
        <h1>usePopcorn</h1>
      </div>
    );
  }

  function Search({ query, setQuery }) {
    const inputEl = useRef(null);

    useEffect(function () {
      function callback(e) {
        if (document.activeElement === inputEl.current) {
          return;
        }
        if (e.code === "Enter") {
          inputEl.current.focus();
          setQuery("");
        }
      }
      document.addEventListener("keydown", callback);
      return () => document.addEventListener("keydown", callback);
    }, []);

    return (
      <input
        className="search"
        type="text"
        placeholder="Search movies..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        ref={inputEl}
      />
    );
  }

  function NumResults({ movies }) {
    return (
      <p className="num-results">
        Found <strong>{movies.length}</strong> results
      </p>
    );
  }

  function Main({ children }) {
    return <main className="main">{children}</main>;
  }

  function Box({ children }) {
    const [isOpen, setIsOpen] = useState(true);

    return (
      <div className="box">
        <button
          className="btn-toggle"
          onClick={() => setIsOpen((open) => !open)}
        >
          {isOpen ? "‚Äì" : "+"}
        </button>

        {isOpen && children}
      </div>
    );
  }

  /*
function WatchedBox() {
  const [watched, setWatched] = useState(tempWatchedData);
  const [isOpen2, setIsOpen2] = useState(true);

  return (
    <div className="box">
      <button
        className="btn-toggle"
        onClick={() => setIsOpen2((open) => !open)}
      >
        {isOpen2 ? "‚Äì" : "+"}
      </button>

      {isOpen2 && (
        <>
          <WatchedSummary watched={watched} />
          <WatchedMoviesList watched={watched} />
        </>
      )}
    </div>
  );
}
*/

  function MovieList({ movies, onSelectMovie }) {
    return (
      <ul className="list list-movies">
        {movies?.map((movie) => (
          <Movie
            movie={movie}
            key={movie.imdbID}
            onSelectMovie={onSelectMovie}
          />
        ))}
      </ul>
    );
  }

  function Movie({ movie, onSelectMovie }) {
    return (
      <li onClick={() => onSelectMovie(movie.imdbID)}>
        <img src={movie.Poster} alt={`${movie.Title} poster`} />
        <h3>{movie.Title}</h3>
        <div>
          <p>
            <span>üóì</span>
            <span>{movie.Year}</span>
          </p>
        </div>
      </li>
    );
  }

  function MovieDetails({ selectedId, onCloseMovie, onAddWatched, watched }) {
    const [movie, setMovie] = useState({});
    const [isLoading, setIsLoading] = useState(false);
    const [userRating, setUserRating] = useState("");

    const countRef = useRef(0);

    useEffect(
      function () {
        if (userRating) countRef.current = countRef.current + 1;
      },
      [userRating]
    );

    const isWatched = watched.map((movie) => movie.imdbID).includes(selectedId);
    const watchedUserRating = watched.find(
      (movie) => movie.imdbID === selectedId
    )?.userRating;

    const {
      Title: title,
      Year: year,
      Poster: poster,
      Runtime: runtime,
      imdbRating,
      Plot: plot,
      Released: released,
      Actors: actors,
      Director: director,
      Genre: genre,
    } = movie;

    /* eslint-disable */
    // if (imdbRating > 8) [isTop, setIsTop] = useState(true);
    // if (imdbRating > 8) return <p>Greatest ever!</p>;

    // const [isTop, setIsTop] = useState(imdbRating > 8);
    // console.log(isTop);
    // useEffect(function () {
    //   setIsTop(true);
    // });

    const isTop = imdbRating > 8;
    console.log(isTop);

    // const [avgRating, setAvgRating] = useState(0);

    function handleAdd() {
      const newWatchedMovie = {
        imdbID: selectedId,
        title,
        year,
        poster,
        imdbRating: Number(imdbRating),
        runtime: Number(runtime.split(" ").at(0)),
        userRating,
        countRatingDecisions: countRef.current,
      };
      onAddWatched(newWatchedMovie);
      onCloseMovie();
      // setAvgRating(Number(imdbRating)); // asynchronous state setting
      // setAvgRating((avgRating + imdbRating) / 2); // => avgRating has not been set yet => imdbRating/2
      // therefore, we have to pass in a callback function because callback will get access to the current value
      // alert(avgRating);
      // setAvgRating((avgRating) => (avgRating + userRating) / 2);
    }

    useEffect(
      function () {
        function callback(e) {
          if (e.code === "Escape") {
            onCloseMovie();
          }
        }

        document.addEventListener("keydown", callback);

        return function () {
          document.removeEventListener("keydown", callback);
        };
      },
      [onCloseMovie]
    );

    useEffect(
      function () {
        async function getMovieDetails() {
          setIsLoading(true);
          const res = await fetch(
            `http://www.omdbapi.com/?apikey=${KEY}&i=${selectedId}`
          );
          const data = await res.json();
          setMovie(data);
          setIsLoading(false);
        }
        getMovieDetails();
      },
      [selectedId]
    );

    useEffect(
      function () {
        if (!title) return;
        document.title = `Movie | ${title}`;
        return function () {
          document.title = "usePopcorn"; // clean up
        };
      },
      [title]
    );

    return (
      <div className="details">
        {isLoading ? (
          <Loader />
        ) : (
          <>
            <header>
              <button className="btn-back" onClick={() => onCloseMovie()}>
                &larr;
              </button>
              <img src={poster} alt={`Poster of ${movie}`} />
              <div className="details-overview">
                <h2>{title}</h2>
                <p>
                  {released} &bull; {runtime}
                </p>
                <p>{genre}</p>
                <p>
                  <span>‚≠êÔ∏è</span>
                  {imdbRating} IMDb rating
                </p>
              </div>
            </header>
            {/* <p>{avgRating}</p> */}
            <section>
              <div className="rating">
                {!isWatched ? (
                  <>
                    <StarRating
                      maxRating={10}
                      size={24}
                      onSetRating={setUserRating}
                    />
                    {userRating > 0 && (
                      <button className="btn-add" onClick={handleAdd}>
                        + Add to list
                      </button>
                    )}
                  </>
                ) : (
                  <p>
                    You rated with movie {watchedUserRating} <span>‚≠êÔ∏è</span>
                  </p>
                )}
              </div>
              <p>
                <em>{plot}</em>
              </p>
              <p>Starring {actors}</p>
              <p>Directed by {director}</p>
            </section>
          </>
        )}
      </div>
    );
  }

  function WatchedSummary({ watched }) {
    const avgImdbRating = average(watched.map((movie) => movie.imdbRating));
    const avgUserRating = average(watched.map((movie) => movie.userRating));
    const avgRuntime = average(watched.map((movie) => movie.runtime));

    return (
      <div className="summary">
        <h2>Movies you watched</h2>
        <div>
          <p>
            <span>#Ô∏è‚É£</span>
            <span>{watched.length} movies</span>
          </p>
          <p>
            <span>‚≠êÔ∏è</span>
            <span>{avgImdbRating}</span>
          </p>
          <p>
            <span>üåü</span>
            <span>{avgUserRating}</span>
          </p>
          <p>
            <span>‚è≥</span>
            <span>{avgRuntime} min</span>
          </p>
        </div>
      </div>
    );
  }

  function WatchedMoviesList({ watched, onDeleteWatched }) {
    return (
      <ul className="list">
        {watched.map((movie) => (
          <WatchedMovie
            movie={movie}
            key={movie.imdbID}
            onDeleteWatched={onDeleteWatched}
          />
        ))}
      </ul>
    );
  }

  function WatchedMovie({ movie, onDeleteWatched }) {
    return (
      <li>
        <img src={movie.poster} alt={`${movie.title} poster`} />
        <h3>{movie.title}</h3>
        <div>
          <p>
            <span>‚≠êÔ∏è</span>
            <span>{movie.imdbRating.toFixed(2)}</span>
          </p>
          <p>
            <span>üåü</span>
            <span>{movie.userRating.toFixed(2)}</span>
          </p>
          <p>
            <span>‚è≥</span>
            <span>{movie.runtime} min</span>
          </p>
          <button
            className="btn-delete"
            onClick={() => onDeleteWatched(movie.imdbID)}
          >
            X
          </button>
        </div>
      </li>
    );
  }
}

